properties.property:
var entries = new java.util.ArrayList();
var warningMsgs = [];

function iterateResult(result, formName) {
  while(result.next()) {
    var xml = convertResultSetToXml(result);
    if (shouldWarnUser(xml["_creation_date"].toString())) {
      warningMsgs.push(formName + " (WARN): " + xml["_uri"].toString());
    } else if (shouldFailForm(xml["_creation_date"].toString())) {
      xml.@form = formName;
      entries.add(xml.toString());
    }
  }
}

var dbConn = DatabaseConnectionFactory.createDatabaseConnection(globalMap.get("jdbcDriver"), globalMap.get("odkJdbcDatabaseUrl"), globalMap.get("dbUser"), globalMap.get("dbPass"));

// visits
var visitResults = dbConn.executeCachedQuery("SELECT _URI, _creation_date, visit_date, derived_from_uri, field_worker_id, round_number, location_id FROM " +  globalMap.get("visitTableName") + " WHERE processed_by_mirth is null AND supervisor_status = 1");
iterateResult(visitResults, "Visit");

// births
var birthResult = dbConn.executeCachedQuery("SELECT _URI, _CREATION_DATE, MOTHER_ID, HOUSEHOLD_NAME, CHILDREN_SECOND_CHILD_SEX, CHILDREN_FIRST_CHILD_SEX, FATHER_ID, CHILDREN_SECOND_CHILD_LAST_NAME, FIRST_LIVE_BIRTH, CHILDREN_SECOND_CHILD_FIRST_NAME, CHILDREN_FIRST_CHILD_LAST_NAME, TOTAL_CHILDREN_BORN, FATHER_NAME, NUM_LIVE_BIRTHS_FROM_RECENT_PREG, CHILDREN_SECOND_CHILD_ID, DERIVED_FROM_URI, CHILDREN_FIRST_CHILD_ID, CHILDREN_FIRST_CHILD_FIRST_NAME, HOUSE_ID, BIRTH_PLACE_OTHER, MOTHER_NAME, UMBILICAL_CORD_APPLIED, REPORTED_BY, RECORDED_DATE, HOUSEHOLD_ID, CHILD_DOB, COLLECTED_BY, UMBILICAL_CORD_CUTTING, BIRTH_PLACE, NUM_LIVE_BIRTHS, MOTHER_LINE_NUMBER FROM " + globalMap.get("birthRegistrationTableName") + " WHERE processed_by_mirth = false AND supervisor_status = '1'");
iterateResult(birthResult, "Birth");

// deaths
var deathResult = dbConn.executeCachedQuery("SELECT _URI, _CREATION_DATE, DERIVED_FROM_URI, BASIC_INFORMATION_HOUSEHOLD_ID, SORCE_OF_INFORMATON_REPORTED_BY, BASIC_INFORMATION_PERMANENT_ID, BASIC_INFORMATION_DATE_OF_DEATH, BASIC_INFORMATION_FIELD_WORKER, BASIC_INFORMATION_DATE_OF_INTERVIEW, BASIC_INFORMATION_PLACE_OF_DEATH, BASIC_INFORMATION_SEX, BASIC_INFORMATION_HOUSE_ID, BASIC_INFORMATION_PLACE_OF_DEATH_OTHER, BASIC_INFORMATION_DECEASED_NAME, BASIC_INFORMATION_HOUSEHOLD_NAME FROM " + globalMap.get("deathRegistrationTableName") + " WHERE processed_by_mirth = false AND supervisor_status = '1'");
iterateResult(deathResult, "Death");

// in migrations
var migrationInResult = dbConn.executeCachedQuery("SELECT _URI, _CREATION_DATE, SECTION5_FR_SEC, SECTION6_MIN_PERSON_FN, SECTION6_MIN_PERSON_LN, RELATIONSHIP_TO_GROUP_HEAD, SECTION5_FR_VILL, SECTION5_FR_PN, PERM_ID, EVER_REG, CURR_HH_ID, COMP_ID, SECTION6_MIN_PERSON_MOTHER_ID, FW, REASONFORMIGRATION, TEMP_ID, SECTION6_MIN_PERSON_BIRTHDATE, SECTION5_FR_HN, MFROM_HH_N, SECTION6_MIN_PERSON_FATHER_ID, FROM_OTHER, SECTION6_SEX, MFROM_HN, DERIVED_FROM_URI, SECTION5_FR_ON, OLD_NAME, MFROM_VILL, SECTION5_FR_HH_N, CURR_HH_N, DATE_MI, CURR_H_N, SECTION6_MIN_PERSON_EDUCATION, MFROM_SEC, DINT, FROM_FIELD, MFROM_PN, REASONFORMIGRATION_OTHER FROM " +  globalMap.get("migrationInTableName") + " WHERE processed_by_mirth = false AND supervisor_status = '1'");
iterateResult(migrationInResult, "In Migration");

// out migrations
var outmigrationResult = dbConn.executeCachedQuery("SELECT _URI, _CREATION_DATE, BASIC_INFO_INTERVIEW_DATE, MOVE_AWAY_FROM_MOVE_AWAY_FROM_HOUSE_NAME, BASIC_INFO_COLLECTED_BY, DERIVED_FROM_URI, MOVE_AWAY_TO_PLACE_MOVED_TO, MOVE_AWAY_TO_PLACE_MOVED_TO_PHONE_NUMBER, MOVE_AWAY_TO_PLACE_MOVED_TO_OTHER, MOVE_AWAY_TO_PLACE_MOVED_TO_SECTION, MOVE_AWAY_TO_PLACE_MOVED_TO_REASON, MOVE_AWAY_TO_PLACE_MOVED_TO_HOUSE_NAME, BASIC_INFO_PERM_ID, MOVE_AWAY_FROM_MOVE_AWAY_FROM_HOUSEHOLD_ID, BASIC_INFO_SEX, MOVE_AWAY_FROM_MOVE_AWAY_FROM_HOUSEHOLD_NAME, MOVE_AWAY_TO_PLACE_MOVED_TO_VILLAGE, MOVE_AWAY_FROM_MOVE_AWAY_FROM_HOUSE_ID, MOVE_AWAY_TO_PLACE_MOVED_TO_REASON_OTHER, BASIC_INFO_MIGRATION_DATE, BASIC_INFO_MIGRANT_NAME FROM " +  globalMap.get("outmigrationTableName") + " WHERE processed_by_mirth = false AND supervisor_status = '1'");
iterateResult(outmigrationResult, "Out Migration");

// pregnancy obs
var obsResult = dbConn.executeCachedQuery("SELECT _URI, _CREATION_DATE, DERIVED_FROM_URI, REPORTBY, HH_NAME, HEALTH_FAC, TT_INJECT, COMP_ID, HH_ID, FW, HEALTH_FAC_OTHER, ATTEND_CLI, PERM_ID_PW, DINT, NAME_PW, FIRST_PG, MPREG, LMP FROM " + globalMap.get("pregnancyRegistrationTableName") + " WHERE processed_by_mirth = false AND supervisor_status = '1'");
iterateResult(obsResult, "Pregnancy Observation");

if (warningMsgs.length > 0) {
  var body = warningMsgs.join("\n");
  alerts.sendAlert(body);
}


dbConn.close();

return entries;

