function reconstructInstanceXml(odkTableName, resultXml, formName) {
	var dbConn = DatabaseConnectionFactory.createDatabaseConnection('com.mysql.jdbc.Driver','jdbc:mysql://localhost/odk_prod','root','test');
	var results = dbConn.executeCachedQuery('SELECT _uri, parent_uri_form_data_model, ordinal_number, element_type, element_name, persist_as_column_name FROM _form_data_model where persist_as_table_name = "' + odkTableName.toUpperCase() +'" ORDER BY _URI');
	if (results.size() == 0) {
		logger.info("No XML elements found for table: " + odkTableName);
		return;
	}

	var instanceXml;
	var elementMap = {};
	while(results.next()) {
		var parentUri = results.getString("parent_uri_form_data_model");

		if (elementMap[parentUri] === undefined) {
			// this is the document (root) element
			instanceXml = new XML('<' + results.getString("element_name") + ' id="' + formName + '"></' + results.getString("element_name") + '>');
			elementMap[results.getString("_uri")] = instanceXml;
		} else {
			var parentElement = elementMap[parentUri];
			if (results.getString("element_type") == "GROUP") {
				// a group element is a container
				var child = new XML('<' + results.getString("element_name") + '/>');
				parentElement.appendChild(child);
				elementMap[results.getString("_uri")] = parentElement[results.getString("element_name")];
			} else {
				// an actual element within the XML
				var propName = results.getString("persist_as_column_name").toLowerCase();
				var value = resultXml[propName].toString();
				if (results.getString("element_type") == "JRDATE") {
					parentElement[results.getString("element_name")] = value.substr(0, 10);
				} else if (propName == "processed_by_mirth" || propName == "form_retired" || propName == "validation_failed") {
					parentElement[results.getString("element_name")] = false;
				} else if (propName == "derived_from_uri") {
					parentElement[results.getString("element_name")] = resultXml["_uri"].toString();
				} else {
					parentElement[results.getString("element_name")] = value;
				}
			}		}
        instanceXml['supervisorStatus'] = 1;
	}

	dbConn.close();
	return instanceXml.toXMLString();
}

function validateVisit(houseId, interviewDate) {
  importPackage(Packages.org.openhds.mirth);

  var visitUrl = "http://" + globalMap.get("openhdsUrl") + "/api/rest/corewebservice/visit/";
  var visitHouse = houseId.toUpperCase();
  var visitDate = interviewDate.substring(0, 10);

  visitUrl +=  visitHouse + "/" + visitDate;

  try {
    var builder = new RestfulRequestBuilder(visitUrl);
    var response = builder.buildGet();

    if (response.getStatusCode() != 200) {
      return false;
    }

    var responseXml = new XML(response.getResponse());
    msg["visitId"] = responseXml["extId"].toString();
    return true;
  } catch(exception) {
    logger.error("Error sending request for visit information, houseid=" + visitHouse +", date=" + visitDate);
  }

  return false;
}

function executeUpdateQuery(updateStr) {
  var dbConn = DatabaseConnectionFactory.createDatabaseConnection(globalMap.get("jdbcDriver"), globalMap.get("odkJdbcDatabaseUrl"), globalMap.get("dbUser"), globalMap.get("dbPass"));
  var result = dbConn.executeUpdate(updateStr);
  dbConn.close();
}

function shouldWarnUser(createDate) {
  // check if the form should be retired
  var timeRegex = /^(\d{4})-(\d{2})-(\d{2})\s(\d{2}):(\d{2})/;
  var matches = timeRegex.exec(createDate);
  if (matches != null) {
    createDate = new Date(matches[1], (matches[2] - 1), matches[3], matches[4], matches[5]);
    var started = createDate.getTime();
    var endDate = started + (globalMap.get("warningTimeout") * 3600000);

    if (endDate - (new Date().getTime()) < 0) {
      return true;
    }
  } else {
    logger.warn("Create date was not set on form instance");
  }
  return false;
}

function sendAuditMessage(origXml, errors) {
  var obj = responseMap.get("Destination 1");
  var msgXml = new XML(obj.getMessage());

  var instanceIdXml = "<formInstanceId>" + instanceId + "</formInstanceId>";
  var validationMsgs = [];

  for(var i = 0; i < errors.length(); i++) {
    validationMsgs.push("<validation-message><message>" + errors[i] + "</message></validation-message>");
  }

  var formXml = "<form-instance>" + instanceIdXml + validationMsgs.join("") + "</form-instance>";

  router.routeMessage("Audit Channel", formXml);
}

function constructAuditXml(instanceXml, errors, formType, formOwnerId, odkUri, formId, derivedFormUri, printToString) {
	var xmlDoc = new XML("<formSubmission />");
	xmlDoc["formType"] = formType;
	xmlDoc["formOwnerId"] = formOwnerId;
	xmlDoc["odkUri"] = odkUri;
	xmlDoc["formId"] = formId;
	xmlDoc["derivedFromUri"] = derivedFormUri;

	if (errors != null) {
	  var xmlErrors = new XML("<formErrors />");
 	  for(var i = 0; i < errors.length(); i++) {
		var xmlError = new XML("<formError><error>" + errors[i] + "</error></formError>");
		xmlErrors.appendChild(xmlError);
	  }
	  xmlDoc.appendChild(xmlErrors);	}

	xmlDoc["formInstanceXml"] = instanceXml.toString();

	if (printToString) {
	  return xmlDoc.toString();
    } else {
      return xmlDoc;
    }
}

function  convertResultSetToXml(rs) {
  var rsmd = rs.getMetaData();
  var result = <result />;

  for (var i = 1; i < rsmd.getColumnCount(); i++)  {
    result[rsmd.getColumnName(i).toLowerCase()] = rs.getString(i);
  }

  return result;
}

function validateLocation(houseId) {
  importPackage(Packages.org.openhds.mirth);

  var locationUrl = "http://" + globalMap.get("openhdsUrl") + "/api/rest/corewebservice/location/";
  locationUrl += houseId.toUpperCase();

  try {
    var builder = new RestfulRequestBuilder(locationUrl);
    var response = builder.buildGet();

    if (response.getStatusCode() != 200) {
      return false;
    }

    return true;
  } catch(exception) {
    logger.error("Error sending request to check location exists for houseid=" + houseId);
  }

  return false;
}

