var dbConn = DatabaseConnectionFactory.createDatabaseConnection(globalMap.get("jdbcDriver"), globalMap.get("odkJdbcDatabaseUrl"), globalMap.get("dbUser"), globalMap.get("dbPass"));
var result = dbConn.executeCachedQuery("SELECT _URI, _CREATION_DATE, MOVE_AWAY_FROM_HOUSE_NAME, BASIC_INFO_GENDER, MOVE_AWAY_TO_PLACE_MOVED_TO, MOVE_AWAY_TO_PLACE_MOVED_TO_PHONE_NUMBER, SUPERVISOR_STATUS, VALIDATION_FAILED, MOVE_AWAY_TO_PLACE_MOVED_TO_OTHER, BASIC_INFO_VISIT_DATE, MOVE_AWAY_FROM_HOUSEHOLD_NAME, MOVE_AWAY_TO_PLACE_MOVED_TO_SECTION, MOVE_AWAY_FROM_HOUSE_ID, MOVE_AWAY_FROM_HOUSEHOLD_ID, MOVE_AWAY_TO_PLACE_MOVED_TO_REASON, MOVE_AWAY_TO_PLACE_MOVED_TO_HOUSE_NAME, DERIVED_FROM_URI, BASIC_INFO_FIRST_NAME, BASIC_INFO_VISIT_ID, BASIC_INFO_INDIVIDUAL_ID, BASIC_INFO_LAST_NAME, MOVE_AWAY_TO_PLACE_MOVED_TO_VILLAGE, PROCESSED_BY_MIRTH, MOVE_AWAY_TO_PLACE_MOVED_TO_REASON_OTHER, BASIC_INFO_FIELD_WORKER_ID, BASIC_INFO_MIGRATION_DATE FROM " +  globalMap.get("outmigrationTableName") + " WHERE processed_by_mirth is null AND supervisor_status is null");
